sdp_rule.sdp_command:
var $TMP = undef
call setHcveVariable('$TMP')
var $dir = nvl(getEnv('TMP'),getEnv('TMPDIR'),'/tmp')
if !testDir('d',$dir)
 return setHcveResult('Cannot access tmp directory')
var $TMP = get_df($dir)
call setHcveResult($TMP)

var $SWP = undef
call setHcveVariable('$SWP')
if !testFile('x','/usr/sbin/swapinfo')
 return setHcveResult('Cannot execute /usr/sbin/swapinfo')
var ($SWP) = command('/usr/sbin/swapinfo -q')
call setHcveResult($SWP)

loop $key (@{CUR.SHLIB})
{loop $dir (split(':',getEnv($key)))
 {next !grepDir($dir,'libXm\.(sl|so)','p')
  var ($fil) = last
  if findCommand('what')
   var ($lin) = grepCommand(concat(last,' ',$fil),'version','fi')
  else
  {call loadString($fil,'Motif Version\s*(\d+(\.\d+){2,})')
   var ($lin) = getLines(0,0)
  }
  if match(last,'version\s*(\d+(\.\d+){2,})',true)
  {var ($ver) = last
   return setHcveResult(cond(compare('valid',$ver,'2.1.0'),\
                             'Adequate',\
                             concat('[',$ver,'] too old')))
  }
  return setHcveResult('No version')
 }
}
return setHcveResult('No library')

call setHcveVariable('$MAX_VER')
if !findCommand('java')
 return setHcveResult('No JVM')
call loadCommand(concat(last,' -version 2>&1'))
var ($str) = grepLastFile('java version "(.*)"','f1')
var $ver = version(replace($str,'[\-\_]','.',true))
if !$ver
 call setHcveResult('No version')

var @tbl = (['11.1.1.1','1.6.0.02'],\
            ['11.1.1.2','1.6.0.05'],\
            ['11.1.1.3','1.6.0.06'],\
            ['11.1.1.4','1.6.0.08'],\
            ['11.1.1.5','1.6.0.09'])
var $txt = concat('[',$str,'] too old')
loop $rec (@tbl)
{if !compare('valid',$ver,$rec->[1])
  return setHcveResult($txt)
 var $txt = concat('[',$str,'] valid for ',$MAX_VER = $rec->[0],' and earlier')
}
return setHcveResult('Adequate')

var $MHZ = undef
call setHcveVariable('$MHZ')
if !testFile('x','/stand/vmunix')
 return setHcveResult('Cannot access [/stand/vmunix]')
if !testFile('x','/dev/mem')
 return setHcveResult('Cannot access [/dev/kmem]')
var $tmp = createTemp('processor')
call writeTemp('processor','itick_per_tick/D')
call closeTemp('processor')
var ($MHZ) = reverse(command(\
  concat('adb -k /stand/vmunix /dev/kmem <',quote($tmp,'x'))))
call unlinkTemp('processor')
var $MHZ = expr('/',field('\s+',1,$MHZ),10000)
call setHcveResult($MHZ)

import $MIDDLEWARE_HOME
if !length($MIDDLEWARE_HOME)
 return setHcveResult('NotSpecified')
if !testDir('d',$MIDDLEWARE_HOME)
 return setHcveResult('NoAccess')
var $DSK = get_df($MIDDLEWARE_HOME)
call setHcveResult($DSK)
call setHcveVariable('$DSK')

var $ver = value(grepLastSql('Version=','f'))
if compare('OLDER',$ver,'11')
{if compare('older',$ver,'10.2.0.4')
  call setHcveResult(concat('[',$ver,'] too old'))
 else
  call setHcveResult('Adequate')
}
else
{if compare('older',$ver,'11.1.0.7')
  call setHcveResult(concat('[',$ver,'] too old'))
 else
  call setHcveResult('Adequate')
}

var $tgt = value(grepLastSql('sga_target=','f'))
if expr('>',$tgt,0)
{var $siz = value(grepLastSql('shared_pool_size=','f'))
 if expr('>',$siz,0)
 {if expr('>',$siz,150994944)
   call setHcveResult('Adequate')
  else
   call setHcveResult(concat('[',$siz,'] too small'))
 }
 else
  call setHcveResult('InternallyDetermined')
}
else
 call setHcveResult('NoSGATarget')

macro check_min
{var (\@tbl,$nam,$min) = @arg
 var $val = value(grepLastSql(concat($nam,'='),'f'))
 if !expr('>=',$val,$min)
  call push(@tbl,concat('[',$val,'] too low for ',$nam))
}

call check_min(\@tbl,'processes',200)
call setHcveResult(cond(scalar(@tbl),join("\012",@tbl),'Adequate'))

macro check_min
{var (\@tbl,$nam,$min) = @arg
 var $val = value(grepLastSql(concat($nam,'='),'f'))
 if !expr('>=',$val,$min)
  call push(@tbl,concat('[',$val,'] too low for ',$nam))
}

var @tbl = ()
call check_min(\@tbl,'processes',500)
call check_min(\@tbl,'open_cursors',500)
call check_min(\@tbl,'session_cached_cursors',100)
call setHcveResult(cond(scalar(@tbl),join("\012",@tbl),'Adequate'))

macro check_min
{var (\@tbl,$nam,$min) = @arg
 var $val = value(grepLastSql(concat($nam,'='),'f'))
 if !expr('>=',$val,$min)
  call push(@tbl,concat('[',$val,'] too low for ',$nam))
}

var @tbl = ()
call check_min(\@tbl,'processes',500)
call check_min(\@tbl,'open_cursors',500)
call check_min(\@tbl,'session_cached_cursors',100)
call setHcveResult(cond(scalar(@tbl),join("\012",@tbl),'Adequate'))

macro check_min
{var (\@tbl,$nam,$min) = @arg
 var $val = value(grepLastSql(concat($nam,'='),'f'))
 if !expr('>=',$val,$min)
  call push(@tbl,concat('[',$val,'] too low for ',$nam))
}

call check_min(\@tbl,'processes',300)
call setHcveResult(cond(scalar(@tbl),join("\012",@tbl),'Adequate'))

macro check_min
{var (\@tbl,$nam,$min) = @arg
 var $val = value(grepLastSql(concat($nam,'='),'f'))
 if !expr('>=',$val,$min)
  call push(@tbl,concat('[',$val,'] too low for ',$nam))
}

call check_min(\@tbl,'processes',500)
call setHcveResult(cond(scalar(@tbl),join("\012",@tbl),'Adequate'))
