sdp_rule.sdp_command:
call setHcveVariable('$OS_FNM')
var $OS_FNM = trim(field(':',1,grepCommand('systeminfo','OS Name','if')))
call setHcveResult($OS_FNM)

var ($MEM,@SYS) = ()
call setHcveVariable('$MEM','@SYS')
if !findCommand('systeminfo')
 return setHcveResult('Cannot access systeminfo')
var @SYS = command('systeminfo')
var $lin = field(':',1,grep(@SYS,'Total Physical Memory:','f'))
if !$lin
 return setHcveResult('Cannot determine Physical Memory')
if match($lin,'\s*(.*)\s*([KGM]B)$')
 var ($MEM,$unt) = (last)
else
 return setHcveResult('Cannot determine Physical Memory')
var $MEM = replace($MEM,'\D','',true)
if compare('eq',$unt,'MB')
 var $MEM = expr('*',$MEM,1024)
elsif compare('eq',$unt,'GB')
 var $MEM = expr('*',$MEM,1048576)
call setHcveResult($MEM)

import @SYS
var $FRE = undef
call setHcveVariable('$FRE')
if !@SYS
 return setHcveResult('Cannot access systeminfo')
var $lin = field(':',1,grep(@SYS,'Available Physical Memory:','f'))
if !$lin
 return setHcveResult('Cannot determine Available Memory')
if match($lin,'\s*(.*)\s*([KGM]B)$')
 var ($FRE,$unt) = (last)
else
 return setHcveResult('Cannot determine Available Memory')
var $FRE = replace($FRE,'\D','',true)
if compare('eq',$unt,'MB')
 var $FRE = expr('*',$FRE,1024)
elsif compare('eq',$unt,'GB')
 var $FRE = expr('*',$FRE,1048576)
call setHcveResult($FRE)

var $dir = nvl(getEnv('TMP'),getEnv('TMPDIR'))
var $TMP = undef
call setHcveVariable('$TMP')
if !testDir('d',$dir)
 return setHcveResult('Cannot access tmp directory')
var $TMP = get_df($dir)
call setHcveResult($TMP)

import @SYS
var $SWP = undef
call setHcveVariable('$SWP')
if !@SYS
 return setHcveResult('Cannot access systeminfo')
var $lin = field(':',2,grep(@SYS,'(Virtual Memory|Page File): Max Size:','f'))
if !$lin
 return setHcveResult('Cannot determine Phyical Memory')
if match($lin,'\s*(.*)\s*([KGM]B)$')
 var ($SWP,$unt) = (last)
else
 return setHcveResult('Cannot determine Phyical Memory')
var $SWP = replace($SWP,'\D','',true)
if compare('eq',$unt,'MB')
 var $SWP = expr('*',$SWP,1024)
elsif compare('eq',$unt,'GB')
 var $SWP = expr('*',$SWP,1048576)
call setHcveResult($SWP)

var $DPT = undef call setHcveVariable('$DPT') macro get_device {var ($dft) = @arg loop $key (grepRegValue('HKEY_LOCAL_MACHINE\SYSTEM\CurrentControlSet\\ Hardware Profiles\UnitedVideo\CONTROL\VIDEO','DefaultSettings.BitsPerPel')) {if match($key,'\\0000$') return $key } return $dft }
# Get the device key var $dft = 'HKLM\SYSTEM\ControlSet001\Hardware Profiles\0001\System\\ CurrentControlSet\SERVICES\IALM\DEVICE0' if ?getRegValue('HKLM\HARDWARE\DEVICEMAP\VIDEO','\Device\Video0') var $dev = replace(last,'^\\(REGISTRY|Registry)\\Machine\\','HKLM\') else var $dev = $dft
# Check the color resolution if !?nvl(getRegValue($dev,'DefaultSettings.BitsPerPel'),\ getRegValue($dev = get_device($dft),'DefaultSettings.BitsPerPel')) return setHcveResult('No color resolution') else var $DPT = hex2dec(last) if !$DPT return setHcveResult('Not found') return setHcveResult(cond(expr('>=',$DPT,8),'Adequate',\ 'Need at least 256 colors'))

import $OS_FNM,$OS_LVL
var $ver = nvl(field('\s+',2,$OS_LVL),0)
if match($OS_FNM,'2003')
 return setHcveResult(cond(expr('>=',$ver,2),'Certified',\
                                             'Needs service pack 2 or later'))
if match($OS_FNM,'2008')
{if match($OS_FNM,'R2')
  return setHcveResult('Certified only for FMW 11.1.1.3+')
 return setHcveResult(cond(expr('>=',$ver,1),'Certified',\
                                             'Needs service pack 1 or later'))
}
return setHcveResult('Not certified')

import $OS_BIT,$OS_FNM,$OS_LVL
var $ver = nvl(field('\s+',2,$OS_LVL),0)
if match($OS_FNM,'2003')
 return setHcveResult(cond(expr('<',$ver,2),     'Needs service pack 2',\
                           expr('==',$OS_BIT,32),'Certified',\
                                                 'Not fully supported'))
if match($OS_FNM,'2008')
{if match($OS_FNM,'R2')
  return setHcveResult('Not fully supported and only for FMW 11.1.1.3+')
 return setHcveResult(cond(expr('<',$ver,1),     'Needs service pack 1',\
                           expr('==',$OS_BIT,32),'Certified',\
                                                 'Not fully supported'))
}
if !expr('==',$OS_BIT,32)
 return setHcveResult('Not certified for Classic')
if match($OS_FNM,'7')
 return setHcveResult('Restricted support and only for FMW 11.1.1.2+')
if match($OS_FNM,'XP.*Professional',true)
 return setHcveResult(cond(expr('<',$ver,3),'Needs service pack 3',\
                                            'Restricted support'))
if match($OS_FNM,'Vista',true)
 return setHcveResult(cond(expr('<',$ver,1),'Needs service pack 1',\
                                            'Restricted support'))
return setHcveResult('Not certified for Classic')

call setHcveVariable('$MAX_VER')
if !findCommand('java')
 return setHcveResult('No JVM')
call loadCommand(concat(last,' -version 2>&1'))
var ($str) = grepLastFile('java version "(.*)"','f1')
var $ver = version(replace($str,'[\-\_]','.',true))
if !$ver
 call setHcveResult('No version')

if grepLastFile('JRockit','f')
 var @tbl = (['11.1.1.1','1.6.0.05'],\
             ['11.1.1.2','1.6.0.14'],\
             ['11.1.1.3','1.6.0.17'],\
             ['11.1.1.4','1.6.0.22'],\
             ['11.1.1.5','1.6.0.24'])
else
 var @tbl = (['11.1.1.1','1.6.0.11'],\
             ['11.1.1.2','1.6.0.14'],\
             ['11.1.1.3','1.6.0.18'],\
             ['11.1.1.4','1.6.0.21'],\
             ['11.1.1.5','1.6.0.24'])

var $txt = concat('[',$str,'] too old')
loop $rec (@tbl)
{if !compare('valid',$ver,$rec->[1])
  return setHcveResult($txt)
 var $txt = concat('[',$str,'] valid for ',$MAX_VER = $rec->[0],' and earlier')
}
return setHcveResult('Adequate')

if !getEnv('JAVA_HOME')
 return setHcveResult('No JAVA_HOME')
if !findCommand('javac')
 return setHcveResult('No JDK')
var $str = field('\s+',1,command(concat(last,' -version 2>&1')))
if version(replace($str,'[\-\_]','.',true))
 call setHcveResult(cond(compare('valid',last,'1.6.0.11'),\
                    'Adequate',\
                    concat('[',$str,'] too old')))
else
 call setHcveResult('No version')

import $MIDDLEWARE_HOME
if !length($MIDDLEWARE_HOME)
 return setHcveResult('NotSpecified')
if !testDir('d',$MIDDLEWARE_HOME)
 return setHcveResult('NoAccess')
var $DSK = get_df($MIDDLEWARE_HOME)
call setHcveResult($DSK)
var $DSK = expr('/',$DSK,1024)
call setHcveVariable('$DSK')

var $ver = value(grepLastSql('Version=','f'))
if compare('OLDER',$ver,'11')
{if compare('older',$ver,'10.2.0.4')
  call setHcveResult(concat('[',$ver,'] too old'))
 else
  call setHcveResult('Adequate')
}
else
{if compare('older',$ver,'11.1.0.7')
  call setHcveResult(concat('[',$ver,'] too old'))
 else
  call setHcveResult('Adequate')
}

var $tgt = value(grepLastSql('sga_target=','f'))
if expr('>',$tgt,0)
{var $siz = value(grepLastSql('shared_pool_size=','f'))
 if expr('>',$siz,0)
 {if expr('>',$siz,150994944)
   call setHcveResult('Adequate')
  else
   call setHcveResult(concat('[',$siz,'] too small'))
 }
 else
  call setHcveResult('InternallyDetermined')
}
else
 call setHcveResult('NoSGATarget')

macro check_min
{var (\@tbl,$nam,$min) = @arg
 var $val = value(grepLastSql(concat($nam,'='),'f'))
 if !expr('>=',$val,$min)
  call push(@tbl,concat('[',$val,'] too low for ',$nam))
}

call check_min(\@tbl,'processes',200)
call setHcveResult(cond(scalar(@tbl),join("\012",@tbl),'Adequate'))

macro check_min
{var (\@tbl,$nam,$min) = @arg
 var $val = value(grepLastSql(concat($nam,'='),'f'))
 if !expr('>=',$val,$min)
  call push(@tbl,concat('[',$val,'] too low for ',$nam))
}

var @tbl = ()
call check_min(\@tbl,'processes',500)
call check_min(\@tbl,'open_cursors',500)
call check_min(\@tbl,'session_cached_cursors',100)
call setHcveResult(cond(scalar(@tbl),join("\012",@tbl),'Adequate'))

